import style from './Card.module.css'

export default function Card({id,name,status,gender,species,origin,image,onClose}) {
   return (
      <div className={style.container}  >
         <div className={style.ButtonDiv}> <button onClick={onClose} className={style.CloseButton}>X</button> </div>
         <div className={style.status}> {status} </div>
         <img src={image} alt='' />
         <h2>Name: {name} ({gender}) </h2>
         <h3>{species} </h3><h4>Origin: {origin} </h4>
         <div id="overlay" onclick="off()">
  <div id="text">Overlay Text</div>
</div>

<div style={{padding:'20px'}}>
  <h2>Overlay with Text</h2>
  <button onclick={on()}>Turn on overlay effect</button>
</div>
<script className={style.text}>  
function on() {
document.getElementById("overlay").style.display = "block"}
function off() {
document.getElementById("overlay").style.display = "none"}
</script>
 </div>
        
   );
}
