import {ADD_FAV, REMOVE_FAV} from ''

export const addFavourite =  (character) =>{
    return{type:ADD_FAVOURITE, payload:character}
}


export const removeFavourite = (id) =>{
    return{type:REMOVE_FAVOURITE, payload:id}
}
