import {ADD_FAV, REMOVE_FAV} from './Action-types'

export const addFavourite =  (character) =>{
    return{type:ADD_FAV, payload:character}
}


export const removeFavourite = (id) =>{
    return{type:REMOVE_FAV, payload:id}
}
